using System;
using System.Linq;
using Shouldly;
using System.Threading.Tasks;
using Volo.Abp.Domain.Repositories;
using Xunit;

namespace Resume.CompanyJobConditions
{
    public class CompanyJobConditionsAppServiceTests : ResumeApplicationTestBase
    {
        private readonly ICompanyJobConditionsAppService _companyJobConditionsAppService;
        private readonly IRepository<CompanyJobCondition, Guid> _companyJobConditionRepository;

        public CompanyJobConditionsAppServiceTests()
        {
            _companyJobConditionsAppService = GetRequiredService<ICompanyJobConditionsAppService>();
            _companyJobConditionRepository = GetRequiredService<IRepository<CompanyJobCondition, Guid>>();
        }

        [Fact]
        public async Task GetListAsync()
        {
            // Act
            var result = await _companyJobConditionsAppService.GetListAsync(new GetCompanyJobConditionsInput());

            // Assert
            result.TotalCount.ShouldBe(2);
            result.Items.Count.ShouldBe(2);
            result.Items.Any(x => x.Id == Guid.Parse("2d3d7700-d568-4527-9128-2ea6a2548ad7")).ShouldBe(true);
            result.Items.Any(x => x.Id == Guid.Parse("0a1a12f4-187e-4577-827c-e146e2c14d37")).ShouldBe(true);
        }

        [Fact]
        public async Task GetAsync()
        {
            // Act
            var result = await _companyJobConditionsAppService.GetAsync(Guid.Parse("2d3d7700-d568-4527-9128-2ea6a2548ad7"));

            // Assert
            result.ShouldNotBeNull();
            result.Id.ShouldBe(Guid.Parse("2d3d7700-d568-4527-9128-2ea6a2548ad7"));
        }

